import React from 'react';
import { EyeOff, Eye } from 'lucide-react';

const LoginPage = () => {
  const [showPassword, setShowPassword] = React.useState(false);
  const [activeTab, setActiveTab] = React.useState('login');

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-purple-800 to-purple-900 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Welcome Section */}
        {activeTab === 'welcome' && (
          <div className="text-center mb-8 p-6">
            <h1 className="text-4xl font-bold text-white mb-4">CryptoCalc</h1>
            <p className="text-purple-200 mb-6">Securing Tech Solutions for a Digital Future</p>
            <button 
              onClick={() => setActiveTab('login')}
              className="w-full bg-purple-600 hover:bg-purple-500 text-white rounded-lg py-3 px-4 transition-all duration-300 mb-4"
            >
              Get Started
            </button>
          </div>
        )}

        {/* Login Section */}
        {activeTab === 'login' && (
          <div className="bg-purple-800/30 backdrop-blur-lg rounded-2xl p-6 shadow-xl border border-purple-700/50">
            <h2 className="text-2xl font-bold text-white mb-6">Log In</h2>
            <div className="space-y-4">
              <div>
                <input
                  type="email"
                  placeholder="Email"
                  className="w-full bg-purple-700/30 text-white placeholder-purple-300 rounded-lg py-3 px-4 focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
              </div>
              <div className="relative">
                <input
                  type={showPassword ? 'text' : 'password'}
                  placeholder="Password"
                  className="w-full bg-purple-700/30 text-white placeholder-purple-300 rounded-lg py-3 px-4 focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
                <button
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 -translate-y-1/2 text-purple-300 hover:text-white transition-colors"
                >
                  {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                </button>
              </div>
              <button className="w-full bg-purple-600 hover:bg-purple-500 text-white rounded-lg py-3 px-4 transition-all duration-300">
                Log in
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default LoginPage;
